import React, { Component } from 'react';
import UpdatedCounter from './withCounter';

 class HOC extends Component {
     constructor(props){
        super(props);
            this.state={
                count:0
            }
     }

    handleClick=()=>{
        //alert("this is HOC");
        this.setState({
            count: this.state.count + 1
        })
    }

    render() {
        return (
            <div>
                <button onClick={this.handleClick}>HOC {this.state.count}</button>
            </div>
        )
    }
}

export default UpdatedCounter(HOC);
