import React from 'react'

  const UpdatedCounter = OriginalComponent => {

    class NewComponent extends React.Component{
        constructor(props){
            super(props);
                this.state={
                    count:0
                }
         }
    
        handleClick=()=>{
            //alert("this is HOC");
            this.setState({
                count: this.state.count + 1
            })
        }
    
    render(){
        return(
            <>
                <OriginalComponent name="vishwas"></OriginalComponent>
            </>
        )
        }
       
}
return NewComponent
}

export default UpdatedCounter
